import React, { useState } from 'react';
import axios from 'axios';
import { RegisterForm } from './components/RegisterForm';
import { LoginForm } from './components/LoginForm';
import { TwoFactorAuthForm } from './components/TwoFactorAuthForm';
import { Title } from './components/Title/Title';

function App() {
  const [step, setStep] = useState('login');
  const [token, setToken] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [error, setError] = useState('');
  const [secret, setSecret] = useState('');


  const handleRegister = async (username, password) => {
    if (!username || !password) {
      setError('Please fill in all fields.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/auth/register', { username, password });
      console.log('Registered successfully!');
      setStep('login');
      setError('');
    } catch (error) {
      if (error.response && error.response.status === 409) {
        setError('User already exists. Please proceed to login.');
      } else {
        console.error('Registration failed:', error);
        setError('Registration failed. Please try again. frontend');
      }
    }
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:5000/auth/login', { username, password });
      const { qrCodeUrl, token, secret } = response.data;
      setToken(token);
      setQrCodeUrl(qrCodeUrl);
      setStep('verify-2fa');
      setError('');
      setSecret(secret);
    } catch (error) {
      console.error('Login failed:', error);
      setError('Invalid username or password.');
    }
  };

  const handleVerify2FA = async (verificationCode) => {
    try {
      const response = await axios.post('http://localhost:5000/auth/verify-2fa', { verificationCode, secret });
      setToken(response.data.token);
      setError('');
      setStep('logged-in');
    } catch (error) {
      console.error('2FA verification failed:', error);
      setError('Invalid 2FA code.');
    }
  };

  const goToRegister = () => {
    setStep('register');
    setError('');
  };

  const goToLogin = () => {
    setStep('login');
    setError('');
  };

  return (
    <div>
      {step === 'register' && (
        <RegisterForm handleRegister={handleRegister} goToLogin={goToLogin}/>
      )}
      {step === 'login' && (
        <LoginForm handleLogin={handleLogin} goToRegister={goToRegister} />
      )}
      {step === 'verify-2fa' && (
        <TwoFactorAuthForm qrCodeUrl={qrCodeUrl} handleVerify2FA={handleVerify2FA} />
      )}
      {step === 'logged-in' && <Title text={"Logged in successfully!"}/>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default App;
