import React, { useState } from 'react';
import axios from 'axios';
import QrCode from 'qrcode.react';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [step, setStep] = useState('register');
  const [error, setError] = useState('');

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:5000/register', { username, password });
      console.log('Registered successfully!');
      setStep('login');
      setError('');
    } catch (error) {
      if (error.response && error.response.status === 409) {
        setError('User already exists. Please proceed to login.');
      } else {
        console.error('Registration failed:', error);
        setError('Registration failed. Please try again.');
      }
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      const { token, otpAuthUrl } = response.data;
      setToken(token);
      setQrCodeUrl(otpAuthUrl);
      setStep('verify-2fa'); // Move to 2FA verification step after successful login
    } catch (error) {
      console.error('Login failed:', error);
      setError('Invalid username or password.');
    }
  };

  const handleVerify2FA = async () => {
    try {
      await axios.post('http://localhost:5000/verify-2fa', { token: verificationCode }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      console.log('Logged in successfully!');
      setStep('logged-in'); // Indicate successful login
    } catch (error) {
      console.error('2FA verification failed:', error);
      setError('Invalid 2FA code.');
    }
  };

  const goToLogin = () => {
    setStep('login');
    setError('');
  };

  return (
    <div>
      {step === 'register' && (
        <div>
          <h2>Register</h2>
          <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          <button onClick={handleRegister}>Register</button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      )}
      {step === 'login' && (
        <div>
          <h2>Login</h2>
          <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          <button onClick={handleLogin}>Login</button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      )}
      {step === 'verify-2fa' && (
        <div>
          <h2>Verify 2FA</h2>
          {qrCodeUrl && <QrCode value={qrCodeUrl} />}
          <input type="text" placeholder="Enter 2FA code" value={verificationCode} onChange={e => setVerificationCode(e.target.value)} />
          <button onClick={handleVerify2FA}>Verify 2FA</button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      )}
      {step === 'logged-in' && (
        <div>
          <h2>Logged in successfully!</h2>
        </div>
      )}
    </div>
  );
}

export default App;
