import React, { useState } from 'react';
import axios from 'axios';

function RegisterForm({ handleRegister }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await handleRegister(username, password);

      // Effectuer la requête HTTP POST vers la route de test après l'enregistrement
      axios.post('http://localhost:5000/test', { message: 'Test POST message' })
        .then(response => {
          console.log('Réponse de la route de test après POST:', response.data);
        })
        .catch(error => {
          console.error('Erreur lors de la récupération de la réponse de test après POST:', error);
        });

    } catch (error) {
      console.error('Registration failed:', error);
      setError('Registration failed. Please try again.');
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
        <button type="submit">Register</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default RegisterForm;
