import React, { useState } from 'react';
import axios from 'axios';
import RegisterForm from './components/RegisterForm';
import LoginForm from './components/LoginForm';
import TwoFactorAuthForm from './components/TwoFactorAuthForm';

function App() {
  const [step, setStep] = useState('register');
  const [token, setToken] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [error, setError] = useState('');
  const [testMessage, setTestMessage] = useState('');

  useEffect(() => {
    // Effectuer la requête HTTP GET vers la route de test lorsque le composant est monté
    axios.get('http://localhost:5000/auth/test')
      .then(response => {
        setTestMessage(response.data); // Met à jour le message de test avec la réponse de la requête
      })
      .catch(error => {
        console.error('Erreur lors de la récupération du message de test:', error);
      });
  }, []);

  const handleRegister = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:5000/register', { username, password });
      console.log('Registered successfully!');
      setStep('login');
      setError('');
    } catch (error) {
      if (error.response && error.response.status === 409) {
        setError('User already exists. Please proceed to login.');
      } else {
        console.error('Registration failed:', error);
        setError('Registration failed. Please try again.');
      }
    }
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      const { token, otpAuthUrl } = response.data;
      setToken(token);
      setQrCodeUrl(otpAuthUrl);
      setStep('verify-2fa');
      setError('');
    } catch (error) {
      console.error('Login failed:', error);
      setError('Invalid username or password.');
    }
  };

  const handleVerify2FA = async (verificationCode) => {
    try {
      await axios.post('http://localhost:5000/verify-2fa', { token: verificationCode }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      console.log('Logged in successfully!');
      setStep('logged-in');
    } catch (error) {
      console.error('2FA verification failed:', error);
      setError('Invalid 2FA code.');
    }
  };

  const goToLogin = () => {
    setStep('login');
    setError('');
  };

  return (
    <div>
      <p>{testMessage}</p>
      {step === 'register' && <RegisterForm handleRegister={handleRegister} />}
      {step === 'login' && <LoginForm handleLogin={handleLogin} goToLogin={goToLogin} />}
      {step === 'verify-2fa' && <TwoFactorAuthForm qrCodeUrl={qrCodeUrl} handleVerify2FA={handleVerify2FA} />}
      {step === 'logged-in' && <h2>Logged in successfully!</h2>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default App;
