import React, { useState } from 'react';
import { Buttons } from '../Buttons/Buttons';
import { Forms } from '../Forms/Forms';
import { Title } from '../Title/Title';
import { Inputs } from '../Inputs/Inputs';

export const RegisterForm = ({ handleRegister, goToLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await handleRegister(username, password);

    } catch (error) {
      console.error('Registration failed:', error);
      setError('Registration failed. Please try again.');
    }
  };

  return (
    <div className='center'>
      <Title text={"Register"}/>
      <Forms onSubmit={handleSubmit}>
        <Inputs type={"text"} placeholder={"Username"} value={username} onChange={e => setUsername(e.target.value)} />
        <Inputs type={"password"} placeholder={"Password"} value={password} onChange={e => setPassword(e.target.value)} />
        <Buttons type={"submit"}>Register</Buttons>
        <Buttons onClick={goToLogin}>Already have an account? Login</Buttons>
      </Forms>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
