import React, { useState } from 'react';
import QrCode from 'qrcode.react';

function TwoFactorAuthForm({ qrCodeUrl, handleVerify2FA }) {
  const [verificationCode, setVerificationCode] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await handleVerify2FA(token, verificationCode);
    } catch (error) {
      console.error('2FA verification failed:', error);
      setError('Invalid 2FA code.');
    }
  };

  return (
    <div>
      <h2>Verify 2FA</h2>
      {qrCodeUrl && (
        <div>
          <QrCode value={qrCodeUrl} />
          <p>Scan this QR code with your authenticator app</p>
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Enter 2FA code" value={verificationCode} onChange={e => setVerificationCode(e.target.value)} />
        <button type="submit">Verify 2FA</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default TwoFactorAuthForm;
