import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { RegisterForm } from './components/RegisterForm/RegisterForm';
import { LoginForm } from './components/LoginForm/LoginForm';
import { TwoFactorAuthForm } from './components/TwoFactorAuthForm/TwoFactorAuthForm';
import { Title } from './components/Title/Title';
import { LogoutButton } from './components/Buttons/Logout';
import { Container } from './components/Container/Container';

function App() {
  const [step, setStep] = useState('login');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [error, setError] = useState('');
  const [secret, setSecret] = useState('');

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setToken(storedToken);
      setStep('logged-in');
    }
  }, []);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:5000/auth/login', { username, password });
      const { qrCodeUrl, token, secret } = response.data;
      setToken(token);
      localStorage.setItem('token', token);
      setQrCodeUrl(qrCodeUrl);
      setStep('verify-2fa');
      setError('');
      setSecret(secret);
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setError(error.response.data.message); // Affichez le message d'erreur renvoyé par le backend
      } else if (error.response && error.response.status === 401) {
        setError('Invalid username or password.');
      } else {
        console.error('Login failed:', error);
        setError('Login failed. Please try again.');
      }
    }
  };

  const handleVerify2FA = async (verificationCode) => {
    try {
      const response = await axios.post('http://localhost:5000/auth/verify-2fa', { verificationCode, secret });
      setToken(response.data.token);
      localStorage.setItem('token', response.data.token);
      setError('');
      setStep('logged-in');
    } catch (error) {
      console.error('2FA verification failed:', error);
      setError('Invalid 2FA code.');
    }
  };

  const handleRegisterSuccess = () => {
    setStep('login');
  };

  const handleLoginSuccess = () => {
    setStep('verify-2fa');
  };

  const goToLogin = () => {
    setStep('login');
  }

  const goToRegister = () => {
    setStep('register');
    setError('');
  };

  return (
    <Container>
      {step === 'register' && (
        <div>
          <RegisterForm onRegister={handleRegisterSuccess} goToLogin={goToLogin}/>
        </div>
      )}
      {step === 'login' && (
        <div>
          <LoginForm handleLogin={handleLogin} goToRegister={goToRegister} />
        </div>
      )}
      {step === 'verify-2fa' && (
        <div>
          <TwoFactorAuthForm qrCodeUrl={qrCodeUrl} handleVerify2FA={handleVerify2FA} />
        </div>
      )}
      {step === 'logged-in' && (
        <div>
          <Title text={"Logged in successfully!"} />
          <LogoutButton setStep={setStep} setToken={setToken} />
        </div>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </Container>
  );
}

export default App;
