import React, { useState } from 'react';
import QrCode from 'qrcode.react';
import { Inputs } from './Inputs/Inputs';
import { Buttons } from './Buttons/Buttons';
import { Forms } from './Forms/Forms';

export const TwoFactorAuthForm = ({ qrCodeUrl, handleVerify2FA }) => {
  const [verificationCode, setVerificationCode] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await handleVerify2FA(verificationCode);
    } catch (error) {
      console.error('2FA verification failed:', error);
      setError('Invalid 2FA code.');
    }
  };

  return (
    <div>
      <Title>Verify 2FA</Title>
      {qrCodeUrl && (
        <div>
          <QrCode value={qrCodeUrl} />
          <p>Scan this QR code with your authenticator app</p>
        </div>
      )}
      <Forms onSubmit={handleSubmit}>
        <Inputs type="text" placeholder="Enter 2FA code" value={verificationCode} onChange={e => setVerificationCode(e.target.value)} />
        <Buttons type="submit">Verify 2FA</Buttons>
      </Forms>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
