import React, { useState } from 'react';
import axios from 'axios';
import { Inputs } from '../Inputs/Inputs';
import { Forms } from '../Forms/Forms';
import { Title } from '../Title/Title';

export const RegisterForm = ({ onRegister }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async (username, password) => {
    if (!username || !password) {
      setError('Please fill in all fields.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/auth/register', { username, password });
      console.log('Registered successfully!');
      onRegister(); // Utiliser la fonction onRegister passée en prop pour changer de step
      setError('');
    } catch (error) {
      if (error.response && error.response.status === 400) {
        console.log(error.response.data.errors);
        setError(error.response.data.message && "Password must contain: " + error.response.data.errors);
      } else if (error.response && error.response.status === 409) {
        setError('User already exists. Please proceed to login.');
      } else {
        console.error('Registration failed:', error);
        setError('Registration failed. Please try again.');
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleRegister(username, password);
  };

  return (
    <div className="register-form">
      <Title text={"Register"}/>
      <Forms onSubmit={handleSubmit}>
        <Inputs 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
        />
        <Inputs 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
        <button type="submit">Register</button>
        {error && <p className='error'>{error}</p>}
      </Forms>
    </div>
  );
};
